import React from "react";
import {View, Text, TouchableOpacity} from "react-native";

export default function home({navigation}) {
    const json = [
        {
            "Nome": "a",
            "Descrição": "1",
            "Imagem": ""
        },
        {
            "Nome": "b",
            "Descrição": "2",
            "Imagem": ""
        },
        {
            "Nome": "c",
            "Descrição": "3",
            "Imagem": ""
        },
        {
            "Nome": "d",
            "Descrição": "4",
            "Imagem": ""
        }   
    ];
    return (
        <View>
            {json.map((item, index) => 
                <TouchableOpacity key={index} onPress={() => {navigation.navigate("Detalhes", item)}}>
                    <Text>{item.Imagem}</Text>
                    <Text>{item.Nome}</Text>
                </TouchableOpacity>
            )}
        </View>
    );
}
