import React, { useState } from 'react'
import { View, Text, TouchableOpacity, TextInput } from 'react-native'
import style from './style.js'

export default function Login({ navigation }) {

    const [user, setUser] = useState('')
    const [password, setPassword] = useState('')

    const validarLogin = () => {
        let url = "http://10.87.207.20:8080/api/equipe";

        let data = {
            "user": user,
            "senha": password
        }

        let resp = fetch(url);
        resp = resp.then(resp => { return resp });
        console.log(resp);
    }

    return (
        <View style={style.page}>
            <View style={style.inputs}>
                <Text>Login:</Text>
                <TextInput style={style.in} placeholder="login" onChange={(e) => { setUser(e.target.value); }} />
            </View>
            <View style={style.inputs}>
                <Text>Senha:</Text>
                <TextInput style={style.in} secureTextEntry={true} onChange={(e) => { setPassword(e.target.value); }} />
            </View>
            <TouchableOpacity onPress={validarLogin}>
                <Text style={style.entrar} >Entrar</Text>
            </TouchableOpacity>
            <View style={style.inputs}>
            </View>
        </View>
    )
}
