import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity } from 'react-native';

export default function Equipe({ route }) {
    const [equipe, setEquipe] = useState({
        "nomeEquipe": "",
        "jogadores": [
            {
                "posicao": "top",
                "nome": ""
            },
            {
                "posicao": "mid",
                "nome": ""
            },
            {
                "posicao": "jg",
                "nome": ""
            },
            {
                "posicao": "adc",
                "nome": ""
            },
            {
                "posicao": "sup",
                "nome": ""
            }
        ],
        "nomeCoach": ""
    });

const { id } = route.params;

useEffect(() => {
    let url = "http://10.87.207.20:8080/api/equipe/" + id;

    fetch(url)
        .then(resp => { return resp.json(); })
        .then(data => { setEquipe(data); })
}, []);

return (
    <View>
        {
            equipe.jogadores.map((item, index) => {
                return (
                    <TouchableOpacity key={index}>
                        <Text>Equipe: {equipe.nomeEquipe}</Text>
                        <Text>Treinador: {equipe.nomeCoach}</Text>
                        <Text>Posição: {item.posicao} </Text>
                        <Text>Jogador: {item.nome} </Text>
                    </TouchableOpacity>
                )
            })
        }
    </View>
);
}
