import React from "react";
import { View, Text, Image, TouchableOpacity, Footter } from "react-native";
import style from './style.js';
import json from '../assets/json.json';

export default function Home({ navigation }) {
    return (
        <View style={style.pag}>
            {json.map((item, index) =>
                <TouchableOpacity style={style.cards} key={index} onPress={() => { navigation.navigate("Detalhes", item) }}>
                    <Text>{item.Nome}</Text>
                    <Image source={{uri: item.Imagem}} style={style.imgH}></Image>
                </TouchableOpacity>
            )}
            <Footter>

            </Footter>
        </View>
    );
}
